  material_describe = #{materialDescribe,jdbcType=VARCHAR},
  material_id = #{materialId,jdbcType=VARCHAR},
  deivice_describe = #{deiviceDescribe,jdbcType=VARCHAR}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, product_nickname, material_describe, material_id, deivice_describe
from material_maintain
where id = #{id,jdbcType=BIGINT}

SELECT * FROM material_maintain where 1=1
AND ( material_describe LIKE "%"#{keywords}"%" or deivice_describe LIKE "%"#{keywords}"%" or product_nickname LIKE "%"#{keywords}"%" )
order by ${orderByClause}

