mapper.select:
select id, product_nickname, material_describe, material_id, device_describe, device_id
from material_maintain
where id = #{id,jdbcType=BIGINT}

SELECT * FROM material_maintain where 1=1
AND ( material_describe LIKE "%"#{keywords}"%" or device_describe LIKE "%"#{keywords}"%" or product_nickname LIKE "%"#{keywords}"%" )
order by ${orderByClause}
